// frontend/src/api.js
import axios from 'axios';

// Base URL of your Flask backend
const API_BASE_URL = 'http://localhost:5000/api';

/**
 * Fetches product trend predictions from Flask backend
 * (works with both dummy & real Flipkart dataset)
 */
export const fetchTrends = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/predict`, {
      headers: { 'Content-Type': 'application/json' },
    });
    return response.data; // { predictions, forecasts, sentiment, wordCloudData }
  } catch (error) {
    console.error('❌ Error fetching data from backend:', error.message);

    // Handle connection errors gracefully
    if (error.code === 'ERR_NETWORK') {
      alert('⚠️ Cannot connect to backend. Please make sure Flask is running on port 5000.');
    }

    return null;
  }
};

/**
 * (Optional) Future-proof method — Fetches detailed product info
 * You can call this later if you add more API routes for products
 */
export const fetchProductDetails = async (productName) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/product/${productName}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching product details:', error);
    return null;
  }
};
