import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import Chart from './Chart';
import WordCloud from './WordCloud';
import SentimentSummary from './SentimentSummary';
import { fetchTrends } from '../api';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      try {
        const result = await fetchTrends();
        if (result) {
          setData(result);
        } else {
          setError('Failed to load data from API');
        }
      } catch (err) {
        setError('API error: ' + err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  if (loading) return <div className="text-center py-10 text-lg">‚öôÔ∏è Loading awesome trends...</div>;
  if (error) return <div className="text-center py-10 text-red-500 text-lg">‚ùå Error: {error}</div>;
  if (!data) return <div className="text-center py-10 text-lg">No data available</div>;

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
      className={`min-h-screen p-8 transition-colors duration-500 ${
        darkMode
          ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white'
          : 'bg-gradient-to-br from-blue-50 via-white to-gray-100 text-gray-900'
      }`}
    >
      {/* Header */}
      <header className="flex flex-col sm:flex-row justify-between items-center mb-8">
        <h1 className="text-4xl font-bold tracking-wide mb-4 sm:mb-0">
          üåü AI Product Trend Predictor
        </h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className={`px-5 py-2 rounded-xl font-semibold transition transform hover:scale-105 ${
            darkMode
              ? 'bg-yellow-400 text-gray-900 hover:bg-yellow-300'
              : 'bg-gray-900 text-white hover:bg-gray-800'
          }`}
        >
          {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </header>

      {/* Insights Summary Bar */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-8">
        <div className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-4 rounded-2xl shadow-md border border-white/20 text-center">
          <p className="text-sm opacity-70">üî• Total Trends</p>
          <h3 className="text-2xl font-bold">{data.forecasts?.length || 0}</h3>
        </div>
        <div className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-4 rounded-2xl shadow-md border border-white/20 text-center">
          <p className="text-sm opacity-70">üí¨ Sentiment</p>
          <h3 className="text-2xl font-bold">{data.sentiment?.avg || 'N/A'}</h3>
        </div>
        <div className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-4 rounded-2xl shadow-md border border-white/20 text-center">
          <p className="text-sm opacity-70">üîë Keywords</p>
          <h3 className="text-2xl font-bold">{data.wordCloudData?.length || 0}</h3>
        </div>
      </div>

      {/* Charts & Visuals */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {data.forecasts && (
          <motion.div
            whileHover={{ scale: 1.03 }}
            className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-5 rounded-2xl shadow-lg border border-white/10"
          >
            <h2 className="text-xl font-semibold mb-3">üìà Forecast Trends</h2>
            <Chart data={data.forecasts} />
          </motion.div>
        )}

        {data.wordCloudData && (
          <motion.div
            whileHover={{ scale: 1.03 }}
            className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-5 rounded-2xl shadow-lg border border-white/10"
          >
            <h2 className="text-xl font-semibold mb-3">‚òÅÔ∏è Keyword Cloud</h2>
            <WordCloud words={data.wordCloudData} />
          </motion.div>
        )}

        {data.sentiment && (
          <motion.div
            whileHover={{ scale: 1.03 }}
            className="backdrop-blur-md bg-white/10 dark:bg-gray-800/40 p-5 rounded-2xl shadow-lg border border-white/10"
          >
            <h2 className="text-xl font-semibold mb-3">üí° Sentiment Overview</h2>
            <SentimentSummary sentiment={data.sentiment.avg} />
          </motion.div>
        )}
      </div>

      {/* Product Predictions */}
      <div className="mt-10">
        <h2 className="text-2xl font-bold mb-4">üß† Product Predictions</h2>
        {data.predictions ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {Object.entries(data.predictions).map(([product, status]) => (
              <motion.div
                key={product}
                whileHover={{ scale: 1.03 }}
                className={`p-4 rounded-2xl shadow-lg border transition backdrop-blur-md ${
                  status === 'Trending'
                    ? 'bg-green-100/30 border-green-400/40 text-green-900 dark:bg-green-900/30 dark:text-green-200'
                    : 'bg-red-100/30 border-red-400/40 text-red-900 dark:bg-red-900/30 dark:text-red-200'
                }`}
              >
                <strong>{product}</strong>
                <p className="text-sm opacity-80">{status}</p>
              </motion.div>
            ))}
          </div>
        ) : (
          <p>No predictions available</p>
        )}
      </div>
    </motion.div>
  );
};

export default Dashboard;
