import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import Chart from './Chart';
import WordCloud from './WordCloud';
import SentimentSummary from './SentimentSummary';
import { fetchTrends } from '../api';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      const result = await fetchTrends();
      setData(result);
      setLoading(false);
    };
    loadData();
  }, []);

  if (loading) return <div className="text-center py-10">Loading awesome trends...</div>;

  return (
    <motion.div 
      initial={{ opacity: 0 }} 
      animate={{ opacity: 1 }} 
      transition={{ duration: 0.5 }}
      className={`min-h-screen p-6 ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}
    >
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">AI Product Trend Predictor</h1>
        <button 
          onClick={() => setDarkMode(!darkMode)} 
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
        >
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </header>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <motion.div whileHover={{ scale: 1.05 }}>
          <Chart data={data.forecasts} />
        </motion.div>
        <motion.div whileHover={{ scale: 1.05 }}>
          <WordCloud words={data.wordCloudData} />
        </motion.div>
        <motion.div whileHover={{ scale: 1.05 }}>
          <SentimentSummary sentiment={data.sentiment.avg} />
        </motion.div>
      </div>

      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Product Predictions</h2>
        <ul className="space-y-2">
          {Object.entries(data.predictions).map(([product, status]) => (
            <li key={product} className={`p-4 rounded-lg ${status === 'Trending' ? 'bg-green-100 dark:bg-green-800' : 'bg-red-100 dark:bg-red-800'}`}>
              <strong>{product}:</strong> {status}
            </li>
          ))}
        </ul>
      </div>
    </motion.div>
  );
};

export default Dashboard;
