import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = () => {
  const { darkMode, setDarkMode } = useTheme();
  const [activeButton, setActiveButton] = useState('Dashboard');
  const [isHovered, setIsHovered] = useState(false);

  const navItems = [
    { name: 'Dashboard', icon: 'ðŸ“Š' },
    { name: 'Analytics', icon: 'ðŸ“ˆ' },
    { name: 'Reports', icon: 'ðŸ“‘' }
  ];

  return (
    <motion.header 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className={`relative shadow-xl transition-colors duration-300
        ${darkMode 
          ? 'bg-gradient-to-r from-purple-900 via-blue-800 to-cyan-700 text-white' 
          : 'bg-gradient-to-r from-blue-100 via-blue-200 to-cyan-100 text-gray-800'
        }`}
    >
      <div className="relative max-w-7xl mx-auto px-4 py-8">
        <div className="flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
          <motion.div>
            <motion.h1 
              className={`text-4xl font-extrabold tracking-tight cursor-pointer
                ${darkMode ? 'drop-shadow-lg' : 'drop-shadow-md'}
              `}
              whileHover={{ scale: 1.05 }}
            >
              <motion.span className="inline-block">
                AI Product Trend
              </motion.span>
              <motion.span 
                className={darkMode ? 'block text-cyan-300' : 'block text-blue-600'}
              >
                Predictor
              </motion.span>
            </motion.h1>
          </motion.div>

          <div className="flex items-center space-x-6">
            {/* Theme Toggle */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={() => setDarkMode(!darkMode)}
              className={`p-2 rounded-full ${
                darkMode ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-yellow-400'
              }`}
            >
              {darkMode ? 'ðŸŒž' : 'ðŸŒ™'}
            </motion.button>

            {/* Navigation */}
            <motion.nav className="hidden md:flex space-x-4">
              {navItems.map((item) => (
                <motion.button
                  key={item.name}
                  onClick={() => setActiveButton(item.name)}
                  className={`px-4 py-2 rounded-lg text-sm transition-colors relative
                    ${darkMode
                      ? activeButton === item.name 
                        ? 'text-white bg-white/20' 
                        : 'text-white/90 hover:text-white hover:bg-white/10'
                      : activeButton === item.name
                        ? 'text-gray-800 bg-gray-800/10'
                        : 'text-gray-600 hover:text-gray-800 hover:bg-gray-800/5'
                    }`}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span className="flex items-center gap-2">
                    {item.icon}
                    {item.name}
                  </span>
                  {activeButton === item.name && (
                    <motion.div
                      className={`absolute inset-0 rounded-lg -z-10 ${
                        darkMode ? 'bg-white/20' : 'bg-gray-800/10'
                      }`}
                      layoutId="activeTab"
                    />
                  )}
                </motion.button>
              ))}
            </motion.nav>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
