import React from 'react';
import WordCloud from 'react-d3-cloud';

const WordCloudComponent = ({ words }) => {
  const data = words.map(word => ({ text: word.text, value: word.value }));

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold mb-4 text-gray-800 dark:text-white">Trending Product Keywords</h3>
      <WordCloud data={data} width={400} height={300} />
    </div>
  );
};

export default WordCloudComponent;
