import React from 'react';
import { motion } from 'framer-motion';

const Header = () => {
  return (
    <motion.header 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className="relative bg-gradient-to-r from-purple-700 via-blue-600 to-cyan-500 text-white"
    >
      {/* Decorative background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-8 -left-8 w-32 h-32 bg-white/10 rounded-full blur-2xl"></div>
        <div className="absolute top-0 right-0 w-48 h-48 bg-white/5 rounded-full blur-3xl"></div>
      </div>

      <div className="relative max-w-7xl mx-auto px-4 py-8">
        <div className="flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
          >
            <motion.h1 
              className="text-4xl font-extrabold tracking-tight"
              whileHover={{ scale: 1.05 }}
            >
              AI Product Trend
              <span className="block text-cyan-300">Predictor</span>
            </motion.h1>
          </motion.div>

          <div className="flex items-center space-x-6">
            <motion.div
              initial={{ opacity: 0, scale: 0.5 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 0.4 }}
              className="flex items-center space-x-2"
            >
              <div className="h-3 w-3 bg-green-400 rounded-full animate-pulse"></div>
              <span className="px-4 py-2 rounded-full bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-colors">
                Real-time Analysis
              </span>
            </motion.div>

            <motion.nav
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.6 }}
              className="hidden md:flex space-x-4"
            >
              {['Dashboard', 'Analytics', 'Reports'].map((item) => (
                <button
                  key={item}
                  className="px-3 py-1 text-sm hover:text-cyan-300 transition-colors"
                >
                  {item}
                </button>
              ))}
            </motion.nav>
          </div>
        </div>
      </div>

      {/* Bottom border gradient */}
      <div className="h-1 bg-gradient-to-r from-cyan-500 via-blue-500 to-purple-600"></div>
    </motion.header>
  );
};

export default Header;
